    {#if pageData && pageData.footer && pageData.footer.address }
      <div class="flex flex-col">
        {#each pageData.footer?.address as address }
          {#if address.url }  
            <a
              class="text-brown text-center font-serif"
              href={address.url}>{address.text}
            </a>
          {:else}
            <p class="text-brown text-center font-serif">{address.text}</p>
          {/if}
        {/each}
      </div>
    {/if}
    {#if pageData && pageData.footer && pageData.footer.paymentLogos}
      <div class="flex flex-row space-x-3">
        {#each pageData.footer.paymentLogos as logo }
          <img class="w-8 object-contain" src={PUBLIC_IMG_URL + logo.logo.url} alt={logo.logo.alt}>
        {/each}
      </div>
    {/if}
    {#if pageData && pageData.footer && pageData.footer.links }
      <div class="flex flex-col md:flex-row md:space-x-5">
        {#each pageData.footer.links as link }
          {#if link.url }
            <a class="text-brown font-serif italic text-center" href="{link.url}">{link.text}</a>
          {:else}
            <p class="text-brown font-serif italic text-center">{link.text}</p>
          {/if}
        {/each}
        <a class="text-brown font-serif italic text-center" href={$page.params.lang ? "/" +$page.params.lang+"/contacts": "/bg" + "/contacts"}>{pageData.contact.pageTitle}</a>
      </div>
    {/if}
  </address>
</footer>